(()=>{document.addEventListener("alpine:init",()=>{Alpine.store("jobFilter",{category:"",location:"",employmentType:"",search:"",reset(){this.category="",this.location="",this.employmentType="",this.search=""}}),Alpine.store("notifications",{items:[],add(e,t="info",s=5e3){let i=Date.now();return this.items.push({id:i,message:e,type:t}),s>0&&setTimeout(()=>this.remove(i),s),i},remove(e){this.items=this.items.filter(t=>t.id!==e)},success(e){return this.add(e,"success")},error(e){return this.add(e,"error")}})});console.log("Recruiting Playbook Frontend loaded");})();
